<!DOCTYPE html><head> <script src="//ajax.googleapis.com/ajax/libs/jquery/1.11.0/jquery.min.js"></script> <script type="text/javascript">	 	 $(document).ready(function(){		 		 $("#csvuploadform").submit(function(event){			 validate(event);		});	 		 	});		function validate(event)	{		var uploadedfile = document.getElementById('fileuploadfield').value;				if(uploadedfile == "")		{			alert("Select a csv file to upload");			event.preventDefault();		}		else if(checkfiles(uploadedfile) !== true)		{			alert("File type uploaded is incorrect. Only csv allowed");			uploadedfile = '';			event.preventDefault();		}		else		{			$("#csvuploadform").submit();		}		}		function checkfiles(file)	{		var fileName = file;		var ext = fileName.substring(fileName.lastIndexOf('.') + 1);				if(ext != "csv")		{			return false;		} 		else		{			return true;		}	} </script></head><body style="padding:1em;font-size:95%;font-family:arial">	<h1>Feeds</h1><div id="upload_form"><form action="csv_upload.php" method="post" enctype="multipart/form-data" id="csvuploadform">	Choose file: <input id="fileuploadfield" type="file" name="csvfile" />&nbsp;<input type="submit" name="submit" value="Upload Feed" /></form></div><div style="width:900px;margin:auto">		<div  style="float:left;width:45%;">				<h3>Current Uploaded Feeds</h3>		<ul id="csvfilelist">		<?php			   if ($handle = opendir('csv')) {				while (false !== ($entry = readdir($handle))) {					if ($entry != "." && $entry != "..") {						echo  "<li>$entry</li>";					}				}				closedir($handle);		   }		?>		</ul>			</div>		<div style="float:right;;width:45%;">		<h3>Segment pixel</h3>		<div style="font-size:12px;">		<?php echo htmlentities('<img src="http://domain.com/dco/pid/[product_id]/exp/[cookie_lifetime].gif?rand=[timestamp]" width="1" height="1" />'); ?>		</div>				<h3>Creative Tag</h3>		<div style="font-size:12px;">		<?php 			echo htmlentities('<script src="http://domain.com/dco/r1ads.js?ts=[timestamp]" type="text/javascript"></script>') . '<br />';			echo htmlentities('<script type="text/javascript">') . '<br />';			echo htmlentities('R1DynamicAd.base = \'http://domain.com/\';') . '<br />';			echo htmlentities('R1DynamicAd.feedid = \'[feed_name]\';') . '<br />';			echo htmlentities('R1DynamicAd.R1GetAds();') . '<br />';			echo htmlentities('R1DynamicAd.click = \'[clickurl]\';') . '<br />';			echo htmlentities('</script>');		?></div>	</div>	<br style="clear:both" /></div></body></html>